 , breed=#{dogInfo.breed}
 , dog_source=#{dogInfo.dogSource}
 , dog_colour=#{dogInfo.dogColour}
 , hair_type=#{dogInfo.hairType}
 , dog_type=#{dogInfo.dogType}
 , dog_level=#{dogInfo.dogLevel}
 , inbreeding=#{dogInfo.inbreeding}
 , tutor=#{dogInfo.tutor}
 , breeder=#{dogInfo.breeder}
 , train_score=#{dogInfo.trainScore}
 , next_train_date=#{dogInfo.nextTrainDateStr}
 , work_place=#{dogInfo.workPlace}
 , work_area=#{dogInfo.workArea}
 , work_city=#{dogInfo.workCity}
