mapper.select:
select * from (
	/*警犬总数*/
	select 'totalDog' as att_name, count(*) as val from t_dog_base  where work_place=#{workUnit}
	union all
	/*民警总数*/
	select 'totalEmp' as att_name, count(*) as val from t_police_user  where work_unit=#{workUnit}
	union all
	/*已训练数*/
	 select 'hasTrain' as att_name, count(*) as val from t_dog_train where train_result != '' and train_start_date >= #{startDate} 
		 and work_unit=#{workUnit}
	union all
	/*待训练数*/
	 select 'waitTrain' as att_name, count(*) as val from t_dog_base where next_train_date >= #{startDate} and next_train_date <= #{endDate} 
		 and work_place=#{workUnit}
	union all
	/*待分配*/
	select 'waitAllot' as att_name, count(*) as val from t_apply_dog where apply_state!=4   and work_unit=#{workUnit}
	union all
	/*已分配*/
	select 'hasAllot' as att_name, count(*) as val from t_apply_dog where apply_state=4   and work_unit=#{workUnit}
	union all
	/*人员类型*/
	select concat('pt_',work_type) as att_name, count(*) as val from t_police_user   where work_unit=#{workUnit}  group by work_type
	union all
	/*专业技能*/
	select concat('pro_', train_name) as att_name, count(*) from (
		select t1.dog_id,t1.train_name, count(*) from t_dog_train t1 join t_dog_base t2 on(t1.dog_id=t2.id)
		where t1.train_result != ''  and t2.work_place=#{workUnit}
		group by t1.train_name,t1.dog_id
	) a group by train_name
	union all
	/*繁育数量*/
	 select 'breedCount' as att_name, count(*) from t_dog_base where dog_source='自繁' and birthday>=#{startDate} and birthday<=#{endDate} 
		 and work_place=#{workUnit}
	union all
	/*淘汰、死亡数量*/
	select case when work_stage=3 then '淘汰' when work_stage=4 then '死亡' end as att_name, count(*) from t_dog_base
	where (work_stage=3 or work_stage=4 )  and work_place=#{workUnit}
	group by work_stage

) tmp where tmp.att_name != ''

select concat(train_name, '(', count(*), '次)') as trainName,dog_id from t_dog_train where train_result != '' and dog_id in
#{item.id}
group by train_name,dog_id

select train_name as trainName,dog_id from t_dog_train where train_unit = 'MAIN' and dog_id in
#{item.id}
group by train_name,dog_id

mapper.update:
update t_dog_base set
	last_update_date = now(),
	tutor=#{dogInfo.policeName},
	dog_name=#{dogInfo.dogName},
	police_id=#{dogInfo.policeId},
	police_name=#{dogInfo.policeName}
	, work_place=#{dogInfo.workPlace}
where id=#{dogInfo.id}

mapper.select:
select * from t_police_user where user_role='GuanLiYuan' and work_unit in (
select work_unit from t_police_user where id in (select police_id from t_dog_train where train_id=#{trainId}) group by work_unit
)

/*人员*/ select concat('ry_', work_type) as tName, count(*) as qty from t_police_user where work_type in('民警', '辅警') and work_unit=#{workUnit} group by work_type union all /*警犬品种*/
select concat('pz_', breed) as tName, count(*) as qty from ( select case WHEN breed='德国牧羊犬' THEN '德国牧羊犬' WHEN breed='昆明犬' THEN '昆明犬' WHEN breed='荷兰牧羊犬' THEN '荷兰牧羊犬' WHEN breed='罗维纳犬' THEN '罗维纳犬' WHEN breed='拉布拉多犬' THEN '拉布拉多犬' WHEN breed='马里努阿犬' THEN '马里努阿犬' WHEN breed='史宾格犬' THEN '史宾格犬' else '其他' end as breed from t_dog_base where work_place=#{workUnit} and work_stage in (1,2) ) a group by breed
union all /*工作犬类别*/ select case when concat('jn_',t1.train_name) is null then 'jn_其他' else concat('jn_',t1.train_name) end as tName, count(1) as qty from (select dog_id, case WHEN train_name='追踪' THEN '追踪' WHEN train_name='鉴别' THEN '鉴别' WHEN train_name='物证搜索' THEN '搜索' WHEN train_name='搜捕' THEN '搜捕' WHEN train_name='治安防范' THEN '治安防范' WHEN train_name='搜爆' THEN '搜爆' WHEN train_name='搜毒' THEN '搜毒' WHEN train_name='救援' THEN '救援' else '其他' end as train_name from t_dog_train) t1 right join t_dog_base t2 on (t1.dog_id=t2.id) where t2.work_place=#{workUnit} and t2.dog_type=1 group by t1.train_name union all /*使用次数*/ select concat('sy_', work_type) as tName, count(*) as qty from t_dog_work where end_time>=#{startDate} and end_time<=#{endDate} and work_unit=#{workUnit} group by work_type union all /*出勤次数*/ select 'cq_安检出勤' as tName, count(*) as qty from ( select xl_num, count(*) from t_dog_work where end_time>=#{startDate} and end_time<=#{endDate} and work_unit=#{workUnit} and work_type='安检' group by xl_num )a union all /*起作用数*/ select concat('work_', work_type) as tName, count(*) as qty from t_dog_work where end_time>=#{startDate} and end_time<=#{endDate} and work_unit=#{workUnit} and is_work='正常' group by work_type union all select 'pa_一般' as tName, count(*) as qty from t_dog_work where aj_level='一般' and end_time>=#{startDate} and end_time<=#{endDate} and work_unit=#{workUnit} union all select 'pa_重特大' as tName, count(*) as qty from t_dog_work where aj_level in('严重', '特大', '重大') and end_time>=#{startDate} and end_time<=#{endDate} and work_unit=#{workUnit}
