values

	(
	#{item.growStage}, #{item.dogId}, #{item.trainId}, #{item.trainDateStr}, #{item.trainClassName}, #{item.trainLevel}, #{item.trainStage}, #{item.trainUnit}, #{item.trainAddr}, #{item.trainUser}, #{item.scoreDetail}, #{item.totalScore}, #{item.nextTrainDateStr}, #{item.policeId}, #{item.mainTrainUser}, #{item.policeName}
	)

mapper.update:
