mapper.sql:
 and t1.work_type=#{vo.workType}
 and t2.dog_name like concat('%', #{vo.dogNameLike}, '%')
 and t1.work_unit=#{vo.workUnit}
 and t1.xl_num=#{vo.xlNum}
 and t1.att_person=#{vo.attPerson}
 and t1.dog_chip_no=#{vo.dogChipNo}
 and t1.case_property=#{vo.caseProperty}
 and t1.case_no=#{vo.caseNo}
 and t1.is_work=#{vo.isWork}
 and t1.security_check_area=#{vo.securityCheckArea}
 and t1.work_result=#{vo.workResult}
 and t1.work_pic=#{vo.workPic}
 and t1.dog_id=#{vo.dogId}

 and t1.att_leader=#{vo.attLeader}
 and t1.aj_per=#{vo.ajPer}
 and t1.aj_addr=#{vor.ajAddr}
 and t1.aj_car=#{vo.ajCar}
 and t1.aj_level=#{vo.ajLevel}
 and t1.search_wp=#{vo.searchWp}
 and t1.dog_work_unit=#{vo.dogWorkUnit}
 and t1.aj_wp=#{vo.ajWp}

  and t1.start_time >= #{vo.startTimeStr} 
 and t1.start_time <= #{vo.endTimeStr} 


	and t2.id in

		#{item.id}



order by t1.start_time desc

mapper.update:
update t_dog_work set last_update_date=now()
 , work_type=#{vo.workType}
 and t1.xl_num=#{vo.xlNum}
 , work_unit=#{vo.workUnit}
 , att_person=#{vo.attPerson}
 , dog_chip_no=#{vo.dogChipNo}
 , case_property=#{vo.caseProperty}
 , case_no=#{vo.caseNo}
 , is_work=#{vo.isWork}
 , security_check_area=#{vo.securityCheckArea}
 , work_result=#{vo.workResult}
 , work_pic=#{vo.workPic}
  , start_time = #{vo.startTimeStr} 
 , end_time = #{vo.endTimeStr} 
, dog_id=#{vo.dogId}

 , att_leader=#{vo.attLeader}
 , aj_per=#{vo.ajPer}
 , aj_addr=#{vor.ajAddr}
 , aj_car=#{vo.ajCar}
 , aj_level=#{vo.ajLevel}
 , search_wp=#{vo.searchWp}
 , dog_work_unit=#{vo.dogWorkUnit}
 , aj_wp=#{vo.ajWp}
where id=#{vo.id}
