mapper.update:
update t_org_config set last_update_date=now()
 , id=#{vo.id}
 , org_name=#{vo.orgName}
 , org_pic=#{vo.orgPic}
 , dog_qty=#{vo.dogQty}
 , org_desc=#{vo.orgDesc}
 , org_leader=#{vo.orgLeader}
 , org_addr=#{vo.orgAddr}
 , org_concat=#{vo.orgConcat}
 , emp_qty=#{vo.empQty}
 , org_area=#{vo.orgArea}
 , last_update_date=#{vo.lastUpdateDate}
where id=#{vo.id}
