mapper.update:
update t_apply_dog set last_update_date=now()
 , id=#{vo.id}
 , apply_type=#{vo.applyType}
 , apply_title=#{vo.applyTitle}
 , apply_state=#{vo.applyState}
 , apply_date=#{vo.applyDate}
 , apply_id=#{vo.applyId}
 , police_id=#{vo.policeId}
 , creation_date=#{vo.creationDate}
 , last_update_date=#{vo.lastUpdateDate}
where id=#{vo.id}

mapper.select:
select * from (
	/*除虫提醒*/
	select 'worm' as att_name, count(*) as val from t_dog_worm where  worm_state != 2 and worm_date<=#{next7Day} 
	union all
	/*免疫提醒*/
	select 'immue' as att_name, count(*) as val from t_dog_immue where  immue_state != 2 and immue_date<=#{next7Day} 
	union all
	/*警犬申请*/
	select 'dogApply' as att_name, count(*) as val from t_apply_dog where apply_state=1
	union all
	/*淘汰申请*/
	select 'tickout' as att_name, count(*) as val from t_dog_tickout where apply_state=#{applyState} /*2*/  and apply_unit=#{workUnit}
	union all
	/*死亡申请*/
	select 'die' as att_name, count(*) as val from t_dog_die where apply_state=#{applyState} /*2*/  and apply_unit=#{workUnit}
	union all
    /*工作申请*/
    select 'work' as att_name, count(*) as val from t_dog_work where work_state='待审批'  and dog_work_unit=#{workUnit}
	union all
	/*培训*/
	select 'train' as att_name, count(*) as val from t_dog_base where  next_train_date>=#{nowDay} and next_train_date<=#{nextMonth} 
		 and work_place=#{workUnit}
		 and police_id=#{policeId}
) tab where tab.val > 0
