mapper.sql:
 and train_name like concat('%', #{vo.trainName}, '%')
 and train_desc=#{vo.trainDesc}
 and train_user=#{vo.trainUser}
 and train_unit like concat('%', #{vo.trainUnit}, '%') 
 and train_addr like concat('%', #{vo.trainAddr}, '%')
  and start_date >= #{vo.startDateStr} 
 and start_date <= #{vo.endDateStr} 
order by start_date desc

mapper.insert:
insert into t_train_config (train_name, train_desc, train_unit, train_addr, start_date, end_date, last_update_date, train_user)
values

	(
	#{item.trainName}, #{item.trainDesc}, #{item.trainUnit}, #{item.trainAddr}, #{item.startDateStr}, #{item.endDateStr}, now(), #{item.trainUser}
	)

mapper.update:
update t_train_config set last_update_date=now()
 , train_name=#{vo.trainName}
 , train_desc=#{vo.trainDesc}
 , train_unit=#{vo.trainUnit}
 , train_addr=#{vo.trainAddr}
 , train_user=#{vo.trainUser}
 , start_date=#{vo.startDateStr}
 , end_date=#{vo.endDateStr}
where id=#{vo.id}
